<span class="line">  <span class="token keyword">return</span> axios<span class="token punctuation">.</span><span class="token function">get</span><span class="token punctuation">(</span><span class="token string">&quot;/user/12345/permissions&quot;</span><span class="token punctuation">)</span><span class="token punctuation">;</span></span>
